import React from "react";

const Modal = ({ show, item, onClose }) => {
  if (!show) return null;

  const {
    title,
    authors,
    description,
    imageLinks,
    publishedDate,
    buyLink,
  } = item.volumeInfo;

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <img
          src={imageLinks?.thumbnail || "https://via.placeholder.com/150"}
          alt={title}
        />
        <h2>{title}</h2>
        <p>
          <strong>Authors:</strong> {authors ? authors.join(", ") : "Unknown"}
        </p>
        <p>
          <strong>Published Date:</strong> {publishedDate || "Unknown"}
        </p>
        <p>
          <strong>Description:</strong> {description || "No description available"}
        </p>
        {buyLink && (
          <a href={buyLink} target="_blank" rel="noopener noreferrer">
            <button className="buy-btn">Buy Now</button>
          </a>
        )}
      </div>
    </div>
  );
};

export default Modal;
