import React, { useState } from "react";
import Modal from "./Modal";

const Card = ({ book }) => {
  const [show, setShow] = useState(false); // State to manage modal visibility
  const [bookItem, setItem] = useState(); // State to manage the selected book item

  return (
    <>
      {book.map((item, index) => {
        console.log
        // Extract required details
        const thumbnail =
          item.volumeInfo.imageLinks &&
          item.volumeInfo.imageLinks.smallThumbnail;
        const title = item.volumeInfo.title;
        const amount =
          item.saleInfo.listPrice && item.saleInfo.listPrice.amount;

        // Render only if thumbnail and amount are defined
        if (thumbnail && amount) {
          return (
            <div
              className="card"
              key={index}
              onClick={() => {
                setShow(true);
                setItem(item);
              }}
            >
              <img src={thumbnail} alt={title} />
              <div className="bottom">
                <h3 className="title">{title}</h3>
                <p className="amount">&#8377;{amount}</p>
              </div>
            </div>
          );
        }
        return null; // Skip rendering for items with missing data
      })}

      {/* Modal Component */}
      {show && <Modal show={show} item={bookItem} onClose={() => setShow(false)} />}
    </>
  );
};

export default Card;
