import React, { useState, useEffect } from "react";
import "../Styles/Main.css";
import Card from "./Card";
import axios from "axios";


const Main = () => {
  const [search, setSearch] = useState("");
  const [bookData, setData] = useState([]);

  // Fetch default books when the component mounts
  useEffect(() => {
    fetchBooks("a"); // Example default query
  }, []);

  // Fetch books function
  const fetchBooks = (query) => {
    axios
      .get(
        `https://www.googleapis.com/books/v1/volumes?q=${query}&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU&maxResults=40`
      )
      .then((res) => setData(res.data.items))
      .catch((err) => console.log(err));
  };

  // Handle search input and fetch books on Enter key
  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      fetchBooks(search); // Fetch books based on the search query
    }
  };

  return (
    <div>
      <div className="search">
        <input
          type="text"
          placeholder="Search for books..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyPress={searchBook}
        />
      </div>
      <div className="book-list">
        {bookData.length > 0 ? (
          bookData.map((book) => (
            <div key={book.id} className="book-item">
              <h3>{book.volumeInfo.title}</h3>
              <p>{book.volumeInfo.authors?.join(", ")}</p>
            </div>
          ))
        ) : (
          <p>No books found</p>
        )}
      </div>
    </div>
  );
};

export default Main;
